<?php

namespace CommonBundle\Repository;

use CommonBundle\Entity\Adm3;
use NewApiBundle\InputType\AdmFilterInputType;

/**
 * Adm4Repository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Adm4Repository extends \Doctrine\ORM\EntityRepository
{
    public function findByAdm3(Adm3 $adm1): array
    {
        return $this->findBy(['adm3' => $adm1], ['name' => 'ASC']);
    }

    public function findByFilter(AdmFilterInputType $filter): array
    {
        return $this->findBy(['id' => $filter->getIds()]);
    }
}
