<?php

namespace Repository;

use Doctrine\ORM\EntityRepository;
use Doctrine\ORM\Tools\Pagination\Paginator;
use InputType\DonorFilterInputType;
use InputType\DonorOrderInputType;
use InvalidArgumentException;
use Request\Pagination;
use Entity\Donor;

/**
 * DonorRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class DonorRepository extends EntityRepository
{
    /**
     * @param DonorOrderInputType|null $orderBy
     * @param Pagination|null $pagination
     * @param DonorFilterInputType|null $filter
     *
     * @return Paginator|Donor[]
     */
    public function findByParams(DonorOrderInputType $orderBy = null, ?Pagination $pagination = null, ?DonorFilterInputType $filter = null): Paginator
    {
        $qb = $this->createQueryBuilder('d');

        if ($pagination) {
            $qb->setMaxResults($pagination->getLimit());
            $qb->setFirstResult($pagination->getOffset());
        }

        if ($filter) {
            if ($filter->hasFulltext()) {
                $qb->andWhere(
                    '(d.fullname LIKE :fulltext OR
                                d.shortname LIKE :fulltext OR
                                d.notes LIKE :fulltext)'
                )
                    ->setParameter('fulltext', '%' . $filter->getFulltext() . '%');
            }
            if ($filter->hasIds()) {
                $qb->andWhere('d.id IN (:ids)')
                    ->setParameter('ids', $filter->getIds());
            }
        }

        if ($orderBy) {
            foreach ($orderBy->toArray() as $name => $direction) {
                switch ($name) {
                    case DonorOrderInputType::SORT_BY_ID:
                        $qb->orderBy('d.id', $direction);
                        break;
                    case DonorOrderInputType::SORT_BY_FULLNAME:
                        $qb->orderBy('d.fullname', $direction);
                        break;
                    case DonorOrderInputType::SORT_BY_SHORTNAME:
                        $qb->orderBy('d.shortname', $direction);
                        break;
                    case DonorOrderInputType::SORT_BY_DATE_ADDED:
                        $qb->orderBy('d.dateAdded', $direction);
                        break;
                    default:
                        throw new InvalidArgumentException('Invalid order by directive ' . $name);
                }
            }
        }

        return new Paginator($qb);
    }
}
